import React, { useState, useEffect } from "react";
import "./styles.css";



const ChatBox = () => {
  const [messages, setMessages] = useState<{ text: string; sender: string }[]>([]);
  const [input, setInput] = useState("");
  const [model, setModel] = useState("DeepSeek Coder");

  useEffect(() => {
    // Unregister any existing service workers
    if (navigator.serviceWorker && navigator.serviceWorker.controller) {
      navigator.serviceWorker.controller.postMessage({
        command: "unregister",
      });
    }
  }, []);

  const sendMessage = () => {
    if (!input.trim()) return;

    try {
      const vscode = (window as any).acquireVsCodeApi();
      vscode.postMessage({ command: "generateCode", prompt: input });

      setMessages([...messages, { text: input, sender: "user" }]);
      setInput("");
    } catch (e) {
      console.error("Failed to send message:", e);
    }
  };

  window.addEventListener("message", (event) => {
    if (event.data.command === "showCode") {
      setMessages([...messages, { text: event.data.code, sender: "bot" }]);
    }
  });

  return (
    <div className="chat-container">
      <div className="chat-header">
        <select value={model} onChange={(e) => setModel(e.target.value)}>
          <option>DeepSeek Coder</option>
          <option>GPT-4</option>
          <option>Claude</option>
        </select>
      </div>
      <div className="chat-messages">
        {messages.map((msg, index) => (
          <div key={index} className={msg.sender === "user" ? "user-message" : "bot-message"}>
            {msg.text}
          </div>
        ))}
      </div>
      <div className="chat-input">
        <input
          type="text"
          placeholder="Type your task here..."
          value={input}
          onChange={(e) => setInput(e.target.value)}
        />
        <button onClick={sendMessage}>âž¤</button>
      </div>
    </div>
  );
};

export default ChatBox;
