import React, { useState, useEffect, useRef } from "react";
import "./styles.css";

const ChatBox = () => {
  const [messages, setMessages] = useState<{ text: string; sender: string }[]>([]);
  const [input, setInput] = useState("");
  const [isTyping, setIsTyping] = useState(false);
  const chatRef = useRef<HTMLDivElement>(null);

  // 🔁 Auto-scroll to bottom on new message or typing
  const scrollToBottom = () => {
    if (chatRef.current) {
      chatRef.current.scrollTo({
        top: chatRef.current.scrollHeight,
        behavior: "smooth", // ← for smooth scroll
      });
    }
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages, isTyping]);

  useEffect(() => {
    const handleMessage = (event: MessageEvent) => {
      if (event.data.command === "showCode") {
        setIsTyping(false);
        setMessages(prev => [...prev, { text: event.data.code, sender: "bot" }]);
      }
    };

    window.addEventListener("message", handleMessage);
    return () => window.removeEventListener("message", handleMessage);
  }, []);

  const sendMessage = () => {
    if (!input.trim()) return;

    const vscode = (window as any).acquireVsCodeApi();
    vscode.postMessage({ command: "generateCode", prompt: input });

    setMessages(prev => [...prev, { text: input, sender: "user" }]);
    setInput("");
    setIsTyping(true);
  };

  return (
    <div className="chatbox-container">
      <div className="chatbox-history" ref={chatRef}>
        {messages.map((msg, index) => (
          <div
            key={index}
            className={`message-bubble ${msg.sender === "user" ? "user-bubble" : "bot-bubble"}`}
          >
            {msg.text}
          </div>
        ))}
        {isTyping && <div className="typing-indicator">CodeGenie is typing...</div>}
      </div>

      <div className="chatbox-input-area">
        <button className="action-button">+</button>
        <button className="action-button">📷</button>
        <input
          className="chatbox-input"
          type="text"
          placeholder="Type your task here (@ to attach files...)"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyDown={(e) => {
            if (e.key === "Enter") sendMessage();
          }}
        />
        <button className="send-button" onClick={sendMessage}>➤</button>
      </div>
    </div>
  );
};

export default ChatBox;
