import React, { useState, useEffect } from "react";
import "./styles.css";

const ChatBox = () => {
  const [messages, setMessages] = useState<{ text: string; sender: string }[]>([]);
  const [input, setInput] = useState("");
  const [mode, setMode] = useState("Code");

  const sendMessage = () => {
    if (!input.trim()) return;

    try {
      const vscode = (window as any).acquireVsCodeApi();
      vscode.postMessage({ command: "generateCode", prompt: input });

      setMessages([...messages, { text: input, sender: "user" }]);
      setInput("");
    } catch (e) {
      console.error("Failed to send message:", e);
    }
  };

  useEffect(() => {
    const handleMessage = (event: MessageEvent) => {
      if (event.data.command === "showCode") {
        setMessages([...messages, { text: event.data.code, sender: "bot" }]);
      }
    };

    window.addEventListener("message", handleMessage);
    return () => window.removeEventListener("message", handleMessage);
  }, [messages]);

  return (
    <div className="full-chat-container">
      <div className="chat-messages">
        {messages.map((msg, index) => (
          <div key={index} className={msg.sender === "user" ? "user-message" : "bot-message"}>
            {msg.text}
          </div>
        ))}
      </div>

      <div className="chat-input-bar">
        <div className="input-actions">
          <button className="icon-button">+</button>
          <button className="icon-button">ðŸ“·</button>
        </div>
        <input
          type="text"
          placeholder="Type your task here..."
          value={input}
          onChange={(e) => setInput(e.target.value)}
        />
        <button className="send-button" onClick={sendMessage}>âž¤</button>
      </div>

      <div className="mode-toggle">
        <label>
          <input
            type="checkbox"
            checked={mode === "Chat"}
            onChange={() => setMode(mode === "Chat" ? "Code" : "Chat")}
          />
          Mode: {mode}
        </label>
      </div>
    </div>
  );
};

export default ChatBox;
