import React, { useState, useEffect, useRef } from "react";
import "./ChatBox.css"; // Keep styles in ChatBox.css

const ChatBox = () => {
  const [messages, setMessages] = useState<{ text: string; sender: string }[]>([]);
  const [input, setInput] = useState("");
  const chatHistoryRef = useRef<HTMLDivElement>(null); // Ref for scrolling

  useEffect(() => {
    // Unregister service workers
    if (navigator.serviceWorker && navigator.serviceWorker.controller) {
      navigator.serviceWorker.controller.postMessage({ command: "unregister" });
    }

    // Scroll to bottom on new messages
    if (chatHistoryRef.current) {
      chatHistoryRef.current.scrollTop = chatHistoryRef.current.scrollHeight;
    }
  }, [messages]);

  const sendMessage = () => {
    if (!input.trim()) return;

    try {
      const vscode = (window as any).acquireVsCodeApi();
      vscode.postMessage({ command: "generateCode", prompt: input });

      setMessages([...messages, { text: input, sender: "user" }]);
      setInput("");
    } catch (e) {
      console.error("Failed to send message:", e);
    }
  };

  window.addEventListener("message", (event) => {
    if (event.data.command === "showCode") {
      setMessages([...messages, { text: event.data.code, sender: "bot" }]);
    }
  });

  return (
    <div className="chat-container">
      <div className="chat-history" ref={chatHistoryRef}>
        {messages.map((msg, index) => (
          <div key={index} className={`message ${msg.sender}`}>
            {msg.text}
          </div>
        ))}
      </div>

      <div className="chat-input-container">
        <input
          type="text"
          className="chat-input"
          placeholder="Type your task here (@ to add files...)"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyDown={(e) => {
            if (e.key === "Enter") {
              sendMessage();
            }
          }}
        />

        <div className="input-buttons">
          <button className="input-button">+</button>
          <button className="input-button">ðŸ“·</button>
          <button className="send-button" onClick={sendMessage}>
            âž¤
          </button>
        </div>
      </div>
    </div>
  );
};

export default ChatBox;
